async function deploy() {
	const [owner] = await hre.ethers.getSigners();
	console.log(owner.address);
	const ad = await hre.ethers.deployContract("AirDropper", [owner.address]);
	await ad.waitForDeployment();
	console.log(
		`AirDropper deployed to ${ad.target}`
	);
}

async function verifyAdContract() {
	const [owner] = await ethers.getSigners();
	await verify(ad_address, [owner.address]);
}

async function verify(address, args) {
	console.log("verifying contract...", address);
	const result = await hre.run("verify:verify", {
		address: ad_address,
		constructorArguments: args
	})
	console.log(result);
}

